"use strict";$(function(){$("#login").validate({rules:{username:{required:!0,minlength:5,maxlength:10},password:{required:!0,minlength:6,maxlength:12}},messages:{username:{required:"请填写用户名信息",minlength:"最少 5 个字符",maxlength:"最多 10 个字符"},password:{required:"请填写密码信息",minlength:"最少 6 个字符",maxlength:"最多 12 个字符"}},submitHandler:function(e){e=$(e).serialize();$.post("/zhang/login.php",e,null,"json").then(function(e){console.log(e),0===e.code?$(".login_error").removeClass("hide"):1===e.code&&(setCookie("nickname",e.nickname),window.location.href="../pages/index.html")})}})});