"use strict";$(function(){$("#register").validate({rules:{username:{required:!0,minlength:6,maxlength:10},password:{required:!0,minlength:5,maxlength:12},nickname:{required:!0,minlength:5,maxlength:10},repeat:{equalTo:"#password"}},messages:{username:{required:"请设置您的账号",minlength:"最少需要填写 6 个字符",maxlength:"最多可以填写 10 个字符"},password:{required:"请设置您的密码",minlength:"最少需要填写 5 个字符",maxlength:"最多可以填写 12 个字符"},nickname:{required:"请设置您的昵称",minlength:"最少需要填写 5 个字符",maxlength:"最多可以填写 10 个字符"},repeat:{equalTo:"两次密码不一致"}},submitHandler:function(e){e=$(e).serialize();$.post("/zhang/registers.php",e,null,"json").then(function(e){console.log(e),0===e.code?$(".login_error").removeClass("hide"):1===e.code&&(setCookie("nickname",e.nickname),window.location.href="../pages/login.html")})}})});